import React, { useState, useEffect } from 'react';
import './ColaboradorForm.css';

const estadosBrasileiros = [
  'AC', 'AL', 'AP', 'AM', 'BA', 'CE', 'DF', 'ES', 'GO', 'MA',
  'MT', 'MS', 'MG', 'PA', 'PB', 'PR', 'PE', 'PI', 'RJ', 'RN',
  'RS', 'RO', 'RR', 'SC', 'SP', 'SE', 'TO'
];

const ColaboradorForm = ({ onSave, colaboradorEditando, limparEdicao }) => {
  const estadoInicial = {
    nome: '', email: '', telefone: '', cpf: '', rg: '', data_nascimento: '',
    genero: '', estado_civil: '', nacionalidade: '', endereco: '', numero: '',
    bairro: '', estado: '', cidade: '', cep: '', cargo: '', pis: '',
    carteira_trabalho: '', status: 'atuando', data_contratacao: '', data_demissao: ''
  };

  const [dados, setDados] = useState(estadoInicial);

  useEffect(() => {
    if (colaboradorEditando) {
      const colaboradorComDatasFormatadas = {
        ...colaboradorEditando,
        data_nascimento: colaboradorEditando.data_nascimento?.split('T')[0] || '',
        data_contratacao: colaboradorEditando.data_contratacao?.split('T')[0] || '',
        data_demissao: colaboradorEditando.data_demissao?.split('T')[0] || ''
      };
      setDados(colaboradorComDatasFormatadas);
    }
  }, [colaboradorEditando]);

  const aplicarMascara = (nome, valor) => {
    let v = valor.replace(/\D/g, '');
    switch (nome) {
      case 'cpf':
        v = v.replace(/(\d{3})(\d)/, '$1.$2')
             .replace(/(\d{3})(\d)/, '$1.$2')
             .replace(/(\d{3})(\d{1,2})$/, '$1-$2');
        break;
      case 'telefone':
        v = v.replace(/(\d{2})(\d)/, '($1) $2')
             .replace(/(\d{5})(\d)/, '$1-$2');
        break;
      case 'cep':
        v = v.replace(/(\d{5})(\d)/, '$1-$2');
        break;
      case 'pis':
        v = v.replace(/(\d{3})(\d{5})(\d{2})(\d)/, '$1.$2.$3-$4');
        break;
      case 'carteira_trabalho':
        v = v.replace(/(\d{7})(\d{2})(\d)/, '$1/$2-$3');
        break;
      default:
        break;
    }
    return v;
  };

  const handleChange = e => {
    const { name, value } = e.target;
    const valor = ['cpf', 'telefone', 'cep', 'pis', 'carteira_trabalho'].includes(name)
      ? aplicarMascara(name, value)
      : value;

    setDados(prev => ({ ...prev, [name]: valor }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (dados.status === 'desligado' && !dados.data_demissao) {
      alert('Informe a data de demissão para colaboradores desligados.');
      return;
    }
    onSave(dados);
  };

  const limparFormulario = () => {
    setDados(estadoInicial);
    limparEdicao();
  };

  return (
    <form onSubmit={handleSubmit} className="formulario">
      <h2>{colaboradorEditando ? 'Editar Colaborador' : 'Cadastrar Colaborador'}</h2>
      <div className="campos">
        {Object.entries(dados).map(([key, val]) => {
          if (key === 'id') return null;
          if (key === 'data_demissao' && dados.status === 'atuando') return null;

          if (key === 'status') {
            return (
              <div key={key} className="campo">
                <label>Status</label>
                <select name="status" value={val} onChange={handleChange} required>
                  <option value="atuando">Atuando</option>
                  <option value="desligado">Desligado</option>
                </select>
              </div>
            );
          }

          if (key === 'estado') {
            return (
              <div key={key} className="campo">
                <label>Estado</label>
                <select name="estado" value={val} onChange={handleChange} required>
                  <option value="">Selecione</option>
                  {estadosBrasileiros.map(sigla => (
                    <option key={sigla} value={sigla}>{sigla}</option>
                  ))}
                </select>
              </div>
            );
          }

          return (
            <div key={key} className="campo">
              <label>{key.replace(/_/g, ' ').toUpperCase()}</label>
              <input
                type={key.includes('data') ? 'date' : 'text'}
                name={key}
                value={val}
                onChange={handleChange}
                required={key !== 'data_demissao'}
              />
            </div>
          );
        })}
      </div>
      <div className="botoes">
        <button type="submit" className="btn-verde">Salvar</button>
        <button type="button" className="btn-verde" onClick={limparFormulario}>Limpar</button>
      </div>
    </form>
  );
};

export default ColaboradorForm;
