import React, { useEffect, useState } from 'react';
import api from '../services/api';
import ColaboradorForm from './ColaboradorForm';

const Dashboard = () => {
  const [colaboradores, setColaboradores] = useState([]);
  const [pesquisa, setPesquisa] = useState('');
  const [editando, setEditando] = useState(null);

  const carregarColaboradores = async () => {
    const res = await api.get('/colaboradores');
    setColaboradores(res.data);
  };

  useEffect(() => {
    carregarColaboradores();
  }, []);

  const salvar = async (dados) => {
    try {
      if (dados.id) {
        await api.put(`/colaboradores/${dados.id}`, dados);
      } else {
        await api.post('/colaboradores', dados);
      }
      carregarColaboradores();
    } catch (e) {
      alert(e.response?.data?.erro || 'Erro ao salvar');
    }
  };

  const excluir = async (id) => {
    if (window.confirm('Deseja excluir este colaborador?')) {
      await api.delete(`/colaboradores/${id}`);
      carregarColaboradores();
    }
  };

  const filtrados = colaboradores.filter(c =>
    Object.values(c).some(v => String(v).toLowerCase().includes(pesquisa.toLowerCase()))
  );

  return (
    <div className="dashboard">
      <ColaboradorForm onSave={salvar} colaboradorEditando={editando} limparEdicao={() => setEditando(null)} />
      <input
        type="text"
        placeholder="Pesquisar por qualquer campo..."
        value={pesquisa}
        onChange={e => setPesquisa(e.target.value)}
        className="input-pesquisa"
      />
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>CPF</th>
            <th>Cargo</th>
            <th>Status</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {filtrados.map(colab => (
            <tr
              key={colab.id}
              style={{ backgroundColor: colab.status === 'atuando' ? '#d4f4d7' : '#f8d4d4' }}
            >
              <td>{colab.nome}</td>
              <td>{colab.cpf}</td>
              <td>{colab.cargo}</td>
              <td>{colab.status}</td>
              <td>
                <button onClick={() => setEditando(colab)}>Editar</button>
                <button onClick={() => excluir(colab.id)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
